mapper.select:
SELECT e.set_date SET_DATE , e.status_code STATUS_CODE , e.anal_point_code ANAL_POINT_CODE , e.item_code ITEM_CODE , ( CASE e.item_code WHEN 'TB01' THEN '탁도' WHEN 'PH01' then 'pH' WHEN 'RC01' then '잔류염소' WHEN 'TE01' then '수온' WHEN 'PN01' then '페놀' WHEN 'SA01' then '시안' WHEN 'TO01' then 'TOC' WHEN 'NH01' then 'NH3-n' ELSE '' END ) ITEM_NAME , ( case e.item_code WHEN 'TB01' then '1' WHEN 'PH01' then '2' WHEN 'RC01' then '3' ELSE '' END ) OUT_ORDER , a.ww_code WW_CODE , ( CASE a.ww_type WHEN 'P' then a.ww_name + '아리수정수센터' ELSE a.ww_name + '수도사업소' END ) WW_NAME , ( CASE c.factory_code WHEN '0' THEN ' ' WHEN '5' THEN '강북' WHEN '6' THEN '자양' WHEN ' ' THEN '' ELSE c.factory_code + '정수장 ' END ) + c.anal_point_name + ( CASE a.ww_type WHEN 'P' then '' ELSE '(' + d.type_name + ')' END ) ANAL_POINT_NAME FROM wb_wellwater a , wb_anal_point c , wb_point_class d , wb_recent_status e WHERE a.ww_code = c.ww_code AND c.point_class_code = d.type_code AND c.anal_point_code = e.anal_point_code AND a.ww_type IN ('P','W') AND c.use_flag='U'
AND a.ww_code= #{WW_CODE}
ORDER BY a.ww_code , e.anal_point_code , out_order

mapper.update:
        UPDATE
        	 wb_recent_status 
       	SET 
       		status_code = #{STATUS_CODE}
			, set_date = TO_CHAR(sys_datetime, 'YYYYMMDDHH24MI')
		WHERE 
			ww_code = #{WW_CODE}
			AND anal_point_code = #{POINT_CODE}
			AND item_code = #{ITEM_CODE}

