mapper.select:
SELECT WAP.anal_point_code POINT_CODE , WAP.ww_code WW_CODE , WAP.anal_point_name POINT_NAME , WAP.water_path WATER_PATH_CODE , WAP.factory_code FACTORY_CODE , WW.ww_name WW_NAME , WPC.type_name TYPE_NAME , WW.ww_type WW_TYPE , ( CASE WW.ww_type WHEN 'P' THEN '정수사업소' WHEN 'W' THEN '수도사업소' ELSE '' END ) WW_TYPE_NAME , ( CASE WW.ww_type WHEN 'P' THEN CASE WAP.factory_code WHEN '9' THEN 'N/A' WHEN '0' THEN '공통' WHEN '1' THEN '1정수장' WHEN '2' THEN '2정수장' WHEN '3' THEN '3정수장' WHEN '4' THEN '정수장_암사' WHEN '5' THEN '정수장_강북' WHEN '6' THEN '정수장_자양' WHEN '7' THEN '정수장_팔당' WHEN '8' THEN '정수장_풍납' ELSE '' END ELSE '' END ) FACTORY_NAME , ( CASE WW.ww_type WHEN 'W' THEN (SELECT ww_name FROM wb_wellwater WHERE ww_code = WAP.water_path AND ROWNUM = 1) ELSE '' END ) WATER_PATH_NAME FROM wb_anal_point WAP , wb_wellwater WW , wb_point_class WPC WHERE WAP.point_class_code = WPC.type_code AND WW.ww_code = WAP.ww_code
AND WW.ww_type = #{WW_TYPE}
AND WW.ww_code = #{WW_CODE}
AND WW.ww_type = 'P' AND WAP.factory_code = #{FACTORY_CODE}
AND WAP.anal_point_code = #{POINT_CODE}
AND WW.ww_type = 'P' AND WAP.anal_point_name = #{POINT_NAME}
AND WW.ww_type = 'W' AND WAP.water_path = #{WATER_PATH_CODE}
AND WAP.use_flag = 'U' ORDER BY WW.ww_type , WW.output_order , WW.ww_code , WAP.factory_code , WAP.anal_point_code

mapper.insert:
        INSERT INTO
        	wb_anal_point (
        		ww_code 
        		, anal_point_code
        		, anal_point_name
        		, factory_code
        		, water_path
        		, point_class_code        		
        		, use_flag
        		, output_order
        		, dis_order
        	)
        VALUES
        	(
        		#{WW_CODE}
        		, RIGHT( '00' + CAST((SELECT MAX(TO_NUMBER(anal_point_code))+1 FROM wb_anal_point) AS VARCHAR(3)), 3)
			
			    , #{PLANT_POINT_NAME}
			    , #{PLANT_FACTORY_CODE}
			    , ''
			    , #{PLANT_POINT_CLASS_CODE}
			
			
			    , #{WATER_POINT_NAME}
			    , ''
			    , #{WATER_PATH_CODE}
			    , #{WATER_POINT_CLASS_CODE}
			    
				, 'U'
				, 1
				, 11
        	)

mapper.update:
        UPDATE
        	wb_anal_point
        SET
        	ww_code = #{WW_CODE}
		        	
        	, anal_point_name = #{PLANT_POINT_NAME}
       		, factory_code = #{PLANT_FACTORY_CODE}
       		, water_path = ''
       		, point_class_code = #{PLANT_POINT_CLASS_CODE}
       	
		
		    , anal_point_name = #{WATER_POINT_NAME}
       		, factory_code = ''
       		, water_path = #{WATER_PATH_CODE}
       		, point_class_code = #{WATER_POINT_CLASS_CODE}
		
		WHERE
			anal_point_code = #{POINT_CODE}

        UPDATE 
        	wb_anal_item 
       	SET
			anal_item_name = #{ITEM_NAME}
			, item_remark =  #{ITEM_REMARK}
			, anal_unit = #{ANAL_UNIT}
			, user_id = #{USER_ID}	
			, use_flag = 
			(
				CASE (SELECT COUNT(item_code) FROM wb_item_standard_1 WHERE item_code = #{ITEM_CODE} AND use_flag = 'U')
					WHEN 0 THEN 'D' 
					ELSE 'U'
				END
			)
		WHERE 
			item_code = #{ITEM_CODE}

        UPDATE 
        	wb_item_standard_1 
       	SET
			update_times = (update_times + 1)				
			, highhigh =  #{HIGH_HIGH}
			, high = #{HIGH}
			, lowlow =  #{LOW_LOW}
			, low = #{LOW}
			, set_date = sys_date	
			, use_flag = #{USE_FLAG}
		WHERE 
			item_code = #{ITEM_CODE}
			AND point_class = #{POINT_CLASS}

