mapper.select:
SELECT COUNT(a.user_id) RECEIVE_COUNT , a.msg MSG , b.start_date START_DATE , b.end_date END_DATE , b.alarm_type ALARM_TYPE , (CASE b.alarm_type WHEN '1' THEN '기준치초과' WHEN '2' THEN '측정상태이상' WHEN '3' THEN '전송상태이상' ELSE '' END) ALARM_MSG FROM wb_smshist a , wb_alarmhist b LEFT OUTER JOIN wb_wellwater c ON B.ww_code = c.ww_code WHERE a.alarm_id = b.alarm_id AND b.ww_code = #{WW_CODE} AND b.start_date BETWEEN #{FROM_DATE} AND #{TO_DATE}
AND b.alarm_type = #{SMS_TYPE}
AND a.msg LIKE '%' + #{SMS_MSG} + '%'
GROUP BY a.msg , b.start_date , b.end_date , b.alarm_type ORDER BY b.start_date desc , b.end_date desc
