mapper.select:
SELECT anal_date ANAL_DATE
,MAX(CASE UPPER(WDM.item_code) WHEN #{ITEM_CODE} THEN WDM.item_val ELSE NULL END) ${ITEM_CODE}
FROM ${TBL_NAME} WDM WHERE WDM.anal_date between #{FROM_DATE} AND #{TO_DATE} AND WDM.anal_point_code = #{POINT_CODE}
AND UPPER(WDM.item_status) = 'OK'
GROUP BY WDM.anal_date ORDER BY WDM.anal_date

SELECT T.anal_date ANAL_DATE
,MAX(CASE T.anal_point_code WHEN #{POINT_CODE} THEN T.${ITEM_CODE} ELSE NULL END) ${ITEM_CODE}_${POINT_CODE}
FROM
( SELECT anal_date ,anal_point_code
,MAX(CASE UPPER(WDM.item_code) WHEN #{ITEM_CODE} THEN WDM.item_val ELSE NULL END) ${ITEM_CODE}
FROM ${TBL_NAME} WDM WHERE WDM.anal_date between #{FROM_DATE} AND #{TO_DATE} AND WDM.anal_point_code in
#{POINT_CODE}
GROUP BY WDM.anal_date , WDM.anal_point_code ) T
(
SELECT anal_date ,anal_point_code
,MAX(CASE UPPER(WDM.item_code) WHEN #{ITEM_CODE} THEN WDM.item_val ELSE NULL END) ${ITEM_CODE}
FROM wb_data_minute_${TBL_ITEMS[1]} WDM WHERE WDM.anal_date between #{FROM_DATE} AND #{TO_DATE} AND WDM.anal_point_code = #{TBL_ITEMS[0]} GROUP BY WDM.anal_date , WDM.anal_point_code
) T
GROUP BY T.anal_date ORDER BY T.anal_date

SELECT anal_date ANAL_DATE
,MAX(CASE UPPER(WDM.item_code) WHEN #{ITEM_CODE} THEN WDM.item_val ELSE NULL END) ${ITEM_CODE}
FROM ${TBL_NAME} WDM WHERE WDM.anal_date between #{FROM_DATE} AND #{TO_DATE} AND WDM.anal_point_code = #{POINT_CODE}
AND UPPER(WDM.item_status) = 'OK'
GROUP BY WDM.anal_date ORDER BY WDM.anal_date

SELECT T.anal_date ANAL_DATE
,MAX(CASE T.anal_point_code WHEN #{POINT_CODE} THEN T.${POINT_ITEM} ELSE NULL END) ${POINT_ITEM}_${POINT_CODE}
FROM
( SELECT anal_date ,anal_point_code
,MAX(CASE UPPER(WDM.item_code) WHEN #{POINT_ITEM} THEN WDM.item_val ELSE NULL END) ${POINT_ITEM}
FROM ${TBL_NAME} WDM WHERE WDM.anal_date between #{FROM_DATE} AND #{TO_DATE} AND WDM.anal_point_code in
#{POINT_CODE}
GROUP BY WDM.anal_date , WDM.anal_point_code ) T
(
SELECT anal_date ,anal_point_code
,MAX(CASE UPPER(WDM.item_code) WHEN #{POINT_ITEM} THEN WDM.item_val ELSE NULL END) ${POINT_ITEM}
FROM wb_data_minute_${TBL_ITEM[1]} WDM WHERE WDM.anal_date between #{FROM_DATE} AND #{TO_DATE} AND WDM.anal_point_code = #{TBL_ITEM[0]} GROUP BY WDM.anal_date , WDM.anal_point_code
) T
GROUP BY T.anal_date ORDER BY T.anal_date

SELECT anal_date ANAL_DATE
,MAX(CASE UPPER(WAP.ww_code) WHEN #{WW_CODE} THEN item_val ELSE null END ) ${ANAL_ITEM}_${WW_CODE}
FROM ${TBL_NAME} WDM , wb_anal_point WAP WHERE WAP.anal_point_code = WDM.anal_point_code AND WDM.anal_date between #{FROM_DATE} AND #{TO_DATE} AND WAP.point_class_code = #{POINT_CLASS_CODE} AND WDM.item_code = #{ANAL_ITEM} GROUP BY WDM.anal_date ORDER BY WDM.anal_date

SELECT anal_date ANAL_DATE
,MAX(CASE UPPER(WAP.point_class_code) WHEN #{POINT_CLASS_CODE} THEN item_val ELSE null END ) ${ANAL_ITEM}_${POINT_CLASS_CODE}
FROM ${TBL_NAME} WDM , wb_anal_point WAP WHERE WAP.anal_point_code = WDM.anal_point_code AND WDM.anal_date between #{FROM_DATE} AND #{TO_DATE} AND WAP.ww_code = #{WW_CODE} AND item_code = #{ANAL_ITEM} GROUP BY WDM.anal_date ORDER BY WDM.anal_date

SELECT
	T.anal_date																					ANAL_DATE

    ,MAX(CASE UPPER(T.anal_point_code) WHEN #{WW_POINT_CODE[1]} THEN item_val ELSE null END )	${ANAL_ITEM}_${WW_POINT_CODE[0]}_${WW_POINT_CODE[1]}

FROM
	(

    (
    	SELECT
    		distinct item_val
    		,item_code
    		,anal_point_code
    		,anal_date
    	FROM
    		${TBL_NAME}_${WW_POINT_CODE[0]}
		WHERE
			anal_date between #{FROM_DATE} AND #{TO_DATE}
			AND anal_point_code = #{WW_POINT_CODE[1]}
			AND item_code = #{ANAL_ITEM}
    )

	) T (item_val, item_code, anal_point_code, anal_date)
GROUP BY
	T.anal_date
ORDER BY
	T.anal_date

    	SELECT
    		anal_date																					ANAL_DATE
    	
    	    ,MAX(CASE UPPER(anal_point_code) WHEN #{WW_POINT_CODE[1]} THEN item_val ELSE null END )		${ANAL_ITEM}_${WW_POINT_CODE[0]}_${WW_POINT_CODE[1]}
    	
    	FROM
    		${TBL_NAME}
    	WHERE
    		anal_date between #{FROM_DATE} AND #{TO_DATE}
    		AND anal_point_code in
    	
    	    #{WW_POINT_CODE[1]}
	    
    		AND item_code = #{ANAL_ITEM}
    	GROUP BY
    		anal_date
    	ORDER BY
    		anal_date
