mapper.select:
SELECT DISTINCT anal_item_val ITEM_VAL , WAI.item_code ITEM_CODE , WAI.item_kind ITEM_KIND , anal_date ANAL_DATE , WDM.anal_point_code POINT_CODE , anal_item_status ITEM_STATUS , WAI.anal_item_name ANAL_ITEM_NAME , WAI.anal_unit ANAL_UNIT , COALESCE(low,-30) LAW_LOWER1 , COALESCE(high,3000000) LAW_UPPER1 , ( CASE WAP.factory_code WHEN '9' THEN 'N/A' WHEN '0' THEN '공통' WHEN '1' THEN '1정수장' WHEN '2' THEN '2정수장' WHEN '3' THEN '3정수장' WHEN '4' THEN '암사' WHEN '5' THEN '강북' WHEN '6' THEN '자양' WHEN '7' THEN '팔당' WHEN '8' THEN '풍납' ELSE '' END ) FACTORY_NAME , WAP.anal_point_name ANAL_POINT_NAME , WIM.point_remark POINT_REMARK , WAP.factory_code FACTORY_CODE , WAI.output_order1 OUTPUT_ORDER1 , WAI.output_order OUTPUT_ORDER , WDM.anal_item_code ANAL_ITEM_CODE FROM wb_anal_item WAI , wb_anal_point WAP , wb_item_standard_1 WIS , wb_anal_master WIM LEFT OUTER JOIN wb_currdata WDM ON WDM.anal_item_code = WIM.item_code AND WDM.anal_point_code = WIM.point_code AND (WDM.anal_item_code <> 'FTA1' OR WDM.anal_item_code <> 'FTA2' ) WHERE WAP.anal_point_name = #{ANAL_POINT_NAME}
AND (WAP.ww_code = #{WW_CODE} or WAP.ww_code = 'PR0183')
AND WAP.ww_code = #{WW_CODE}
AND WIM.point_code = WAP.anal_point_code AND WAI.item_code = WIS.item_code AND WIM.item_code = WAI.item_code AND WIS.point_class = WAP.point_class_code AND WAI.item_code = WIM.item_code AND WDM.ww_code = WAP.ww_code ORDER BY WAI.item_kind , WAP.factory_code
, WAI.output_order1
, WAI.output_order

SELECT T.anal_point_code POINT_CODE , T.anal_date ANAL_DATE , WAP.anal_point_name POINT_NAME , WPC.type_name TYPE_NAME , WAP.dis_order DIS_ORDER , max(case upper(T.item_code) when 'TB01' then T.item_val else null end) TB01 , max(case upper(T.item_code) when 'TB02' then T.item_val else null end) TB02 , max(case upper(T.item_code) when 'PH01' then T.item_val else null end) PH01 , max(case upper(T.item_code) when 'RC01' then T.item_val else null end) RC01 , max(case upper(T.item_code) when 'TE01' then T.item_val else null end) TE01 , max(case upper(T.item_code) when 'CD01' then T.item_val else null end) CD01 FROM (
( SELECT anal_item_val , anal_item_code , anal_point_code, max(anal_date) FROM wb_currdata where upper(anal_item_code) = 'TB01' or upper(anal_item_code) = 'TB02' or upper(anal_item_code) = 'PH01' or upper(anal_item_code) = 'RC01' or upper(anal_item_code) = 'TE01' or upper(anal_item_code) = 'CD01' group by anal_point_code, anal_item_code )
) T(item_val, item_code, anal_point_code, anal_date) , wb_anal_point WAP , wb_point_class WPC , wb_wellwater WW WHERE T.anal_point_code = WAP.anal_point_code AND WPC.type_code = WAP.point_class_code AND WAP.ww_code = WW.ww_code AND WAP.water_path = #{WW_CODE} GROUP BY T.anal_point_code , WAP.anal_point_name , WPC.type_name,dis_order ORDER BY dis_order, POINT_NAME
